import{_ as a,c as i,G as l,b as e}from"./chunks/framework.BrC0DRXd.js";const t=JSON.parse('{"title":"模块总结 生物与环境","description":"生物选择性必修二 生物与环境","frontmatter":{"title":"模块总结 生物与环境","tags":["模块总结"],"categories":["生物"],"date":"2025-11-28T00:00:00.000Z","description":"生物选择性必修二 生物与环境","articleGPT":"人机。","references":[{"title":null,"url":null}],"top":false,"head":[["link",{"rel":"canonical","href":"https://blog.xernhe.com/posts/25112803"}]]},"headers":[],"relativePath":"posts/25112803.md","filePath":"posts/25112803.md","lastUpdated":1771162871000}');const s=a({name:"posts/25112803.md"},[["render",function(a,t,s,n,r,o){return e(),i("div",null,t[0]||(t[0]=[l('<h2 id="第-1-章-种群及其动态" tabindex="-1">第 1 章 种群及其动态 <a class="header-anchor" href="#第-1-章-种群及其动态" aria-label="Permalink to &quot;第 1 章  种群及其动态&quot;">​</a></h2><p><strong>种群：在一定的空间范围内，同种生物所有个体形成的集合</strong>。</p><hr><p>种群的基本特征：</p><ul><li>种群的数量特征：种群密度；</li><li>种群的空间特征：随机分布、均匀分布、集群分布；</li><li>种群的遗传特征：进化、遗传、繁殖的单位，拥有基因库。</li></ul><h3 id="第-1-节-种群的数量特征" tabindex="-1">第 1 节 种群的数量特征 <a class="header-anchor" href="#第-1-节-种群的数量特征" aria-label="Permalink to &quot;第 1 节  种群的数量特征&quot;">​</a></h3><p>样方法的取样方式：</p><ul><li>五点取样法；</li><li>等距取样法。</li></ul><hr><p>调查种群密度的高级方法：</p><ul><li>红外触发相机；</li><li>分析粪便；</li><li>标记声音。</li></ul><h3 id="第-2-节-种群数量的变化" tabindex="-1">第 2 节 种群数量的变化 <a class="header-anchor" href="#第-2-节-种群数量的变化" aria-label="Permalink to &quot;第 2 节  种群数量的变化&quot;">​</a></h3><p>种群增长率：单位时间内种群数量增长的百分比；</p><p>种群增长速率：单位时间内种群数量变化的数量。</p><p><a class="img-fancybox" href="https://xernhe.oss-cn-beijing.aliyuncs.com/img/20251128223758.png" data-fancybox="gallery" data-caption=""><img class="post-img" src="https://xernhe.oss-cn-beijing.aliyuncs.com/img/20251128223758.png" alt="" loading="lazy"><span class="post-img-tip"></span></a></p><h2 id="第-3-章-生态系统及其稳定性" tabindex="-1">第 3 章 生态系统及其稳定性 <a class="header-anchor" href="#第-3-章-生态系统及其稳定性" aria-label="Permalink to &quot;第 3 章  生态系统及其稳定性&quot;">​</a></h2><h3 id="生态系统的能量流动" tabindex="-1">生态系统的能量流动 <a class="header-anchor" href="#生态系统的能量流动" aria-label="Permalink to &quot;生态系统的能量流动&quot;">​</a></h3><p><a class="img-fancybox" href="https://xernhe.oss-cn-beijing.aliyuncs.com/img/20260118211951.png" data-fancybox="gallery" data-caption=""><img class="post-img" src="https://xernhe.oss-cn-beijing.aliyuncs.com/img/20260118211951.png" alt="" loading="lazy"><span class="post-img-tip"></span></a></p><p><a class="img-fancybox" href="https://xernhe.oss-cn-beijing.aliyuncs.com/img/20260118212056.png" data-fancybox="gallery" data-caption=""><img class="post-img" src="https://xernhe.oss-cn-beijing.aliyuncs.com/img/20260118212056.png" alt="" loading="lazy"><span class="post-img-tip"></span></a></p><h3 id="生态系统的信息传递" tabindex="-1">生态系统的信息传递 <a class="header-anchor" href="#生态系统的信息传递" aria-label="Permalink to &quot;生态系统的信息传递&quot;">​</a></h3><p><strong>生命活动的正常进行，离不开信息的作用；生物种群的繁衍，也离不开信息的传递。信息还能够调节生物的种间关系，进而维持生态系统的平衡与稳定。</strong></p><h3 id="生态系统的稳定性" tabindex="-1">生态系统的稳定性 <a class="header-anchor" href="#生态系统的稳定性" aria-label="Permalink to &quot;生态系统的稳定性&quot;">​</a></h3><p>生态平衡的生态系统具有以下特性：</p><ul><li>结构平衡；</li><li>功能平衡；</li><li>收支平衡。</li></ul><h2 id="第-4-章-人与环境" tabindex="-1">第 4 章 人与环境 <a class="header-anchor" href="#第-4-章-人与环境" aria-label="Permalink to &quot;第 4 章  人与环境&quot;">​</a></h2><h3 id="生态工程" tabindex="-1">生态工程 <a class="header-anchor" href="#生态工程" aria-label="Permalink to &quot;生态工程&quot;">​</a></h3><p>遵循着<strong>整体、协调、循环、自生</strong>等生态学基本原理。</p>',27)]))}]]);export{t as __pageData,s as default};
